import React, { useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, BarElement, CategoryScale, LinearScale } from 'chart.js';

ChartJS.register(BarElement, CategoryScale, LinearScale);

function App() {
  const [skills, setSkills] = useState('');
  const [matches, setMatches] = useState([]);

  const handleSubmit = async () => {
    try {
      const res = await axios.post('http://localhost:5000/api/match', { skills });
      setMatches(res.data.roles);
    } catch (err) {
      console.error('Error:', err);
    }
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial' }}>
      <h1>SkillBridge</h1>
      <textarea
        rows="5"
        cols="50"
        value={skills}
        onChange={e => setSkills(e.target.value)}
        placeholder="Enter your skills, separated by commas..."
      />
      <br />
      <button onClick={handleSubmit} style={{ marginTop: '1rem' }}>Analyze</button>

      <h2>Matched Roles:</h2>
      <ul>
        {matches.map((match, idx) => (
          <li key={idx} style={{ marginBottom: '1rem' }}>
            <strong>{match.role}</strong><br />
            <em>{match.description}</em>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
